const CourseDetail = () => {
  const { courseId } = useParams();
  const course = coursesData.find(c => c.id === parseInt(courseId));

  if (!course) {
    return <h2>Course not found</h2>;
  }

  return (
    <Container className="my-5">
      <Row>
        <Col md={8}>
          <Card>
            <Card.Img variant="top" src={course.imageUrl} />
            <Card.Body>
              <Card.Title>{course.title}</Card.Title>
              <Card.Text>{course.description}</Card.Text>
              <Card.Text className="font-weight-bold">{course.price}</Card.Text>
              <Button variant="primary">Enroll Now</Button>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Instructor</Card.Title>
              <Row>
                <Col md={4}>
                  <img src={course.instructor.imageUrl} alt={course.instructor.name} className="img-fluid rounded-circle" />
                </Col>
                <Col md={8}>
                  <h5>{course.instructor.name}</h5>
                  <p>{course.instructor.bio}</p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Link to="/" className="btn btn-secondary mt-3">Back to Catalog</Link>
      <Link to="/dashboard" className="btn btn-secondary mt-3">Go to Dashboard</Link>

    </Container>
  );
};

export default CourseDetail;
